import React, { useState } from 'react';

const Form = (props) => {
    const { inputs, setInputs } = props;

    const [color, setColor] = useState("");

    const handleSubmit = (event) => {
        event.preventDefault();

        const Box = {
            color: color,
            size: 20,
        };

        setColor([...inputs, Box]);
    }

    return(
        <div>
            <form onSubmit={(event) =>{
                handleSubmit(event);
            }}>
                <label htmlFor="color">Color</label>
                <input onChange={(event) => setColor(event.target.value)} type="text"></input>
                <button>Add</button>
            </form>
        </div>
    )
}

export default Form
